mapper.select:
SELECT COALESCE(ROUND(AVG(gradePoint), 1), 0.0) 
FROM review 
WHERE productCode = #{productCode}

SELECT COUNT(*) 
FROM review
WHERE productCode = #{productCode}

SELECT * FROM review


        AND productCode = #{productCode}


ORDER BY idx DESC

mapper.update:
UPDATE purchase 
SET isReview = 'y' 
WHERE idx = #{idx}

mapper.select:
SELECT idx, order_code as orderCode, product_code as productCode
FROM order_detail_table 
