mapper.select:
SELECT * FROM users
user_role = #{_parameter}
ORDER BY CASE WHEN user_role = 'admin' THEN 0 ELSE 1 END, join_date DESC

mapper.update:
UPDATE users
SET
    user_name = #{user_name},
    user_role = #{user_role},
    user_email = #{user_email},
    user_tel = #{user_tel},
    user_add = #{user_add}


        , user_pw = #{user_pw}



        , user_age = #{user_age}
        , user_gender = #{user_gender}
        , user_birthday = #{user_birthday}
        , recommender_id = #{recommender_id}



        , biz_reg_no = #{biz_reg_no}
        , company_name = #{company_name}
        , ceo_name = #{ceo_name}
        , biz_type = #{biz_type}

            , biz_license_file = #{biz_license_file}


WHERE idx = #{idx}

mapper.insert:
INSERT INTO users (
    user_id, user_pw, user_name, user_tel, user_email, user_add, user_role
) VALUES (
    #{user_id}, #{user_pw}, #{user_name}, #{user_tel}, #{user_email}, #{user_add}, 'business'
)

INSERT INTO business_users (
    businessId, bizRegNo, companyName, ceoName, bizType, bizLicenseFile
) VALUES (
    #{businessId}, #{bizRegNo}, #{companyName}, #{ceoName}, #{bizType}, #{bizLicenseFile}
)
